class Text3D{constructor(){this.scene=new THREE.Scene,this.camera=new THREE.PerspectiveCamera(75,window.innerWidth/window.innerHeight,.1,1e3),this.renderer=new THREE.WebGLRenderer,this.mouse=new THREE.Vector2,this.targetRotation=new THREE.Vector2,this.lerpFactor=.1,this.init()}init(){this.setupRenderer(),this.setupLights(),this.setupCamera(),this.loadFontAndCreateText(),this.addMouseMoveListener(),document.body.appendChild(this.renderer.domElement),this.animate()}setupRenderer(){this.renderer.setSize(window.innerWidth,window.innerHeight)}setupLights(){let e=new THREE.AmbientLight(16777215,.5);this.scene.add(e);let t=new THREE.DirectionalLight(1048575,1);t.position.set(1,1,1).normalize(),this.scene.add(t)}setupCamera(){this.camera.position.z=5}loadFontAndCreateText(){let e=new THREE.FontLoader;e.load("https://threejs.org/examples/fonts/droid/droid_sans_bold.typeface.json",e=>{let t=this.createTextGeometry(e);this.centerTextGeometry(t);let i=new THREE.MeshStandardMaterial({color:30719});this.textMesh=new THREE.Mesh(t,i),this.scene.add(this.textMesh)})}createTextGeometry(e){return new THREE.TextGeometry("3D",{font:e,size:1.5,height:.4,curveSegments:12,bevelEnabled:!0,bevelThickness:0,bevelSize:.01,bevelOffset:0,bevelSegments:5})}centerTextGeometry(e){e.computeBoundingBox();let t=e.boundingBox;e.translate(-.5*(t.max.x-t.min.x),-.5*(t.max.y-t.min.y),-.5*(t.max.z-t.min.z))}addMouseMoveListener(){document.addEventListener("mousemove",e=>this.onMouseMove(e),!1)}onMouseMove(e){this.mouse.x=e.clientX/window.innerWidth*2-1,this.mouse.y=-(2*(e.clientY/window.innerHeight))+1,this.targetRotation.x=-this.mouse.y*Math.PI/4,this.targetRotation.y=-this.mouse.x*Math.PI/4}animate(){requestAnimationFrame(()=>this.animate()),this.updateTextRotation(),this.renderer.render(this.scene,this.camera)}updateTextRotation(){this.textMesh&&(this.textMesh.rotation.x+=(this.targetRotation.x-this.textMesh.rotation.x)*this.lerpFactor,this.textMesh.rotation.y+=(this.targetRotation.y-this.textMesh.rotation.y)*this.lerpFactor)}}const text3D=new Text3D;